#ifndef LLVM_LIB_TARGET_WORUYU_WORUYUISELDAGTODAG_H
#define LLVM_LIB_TARGET_WORUYU_WORUYUISELDAGTODAG_H

#include "woruyuTargetMachine.h"
#include "llvm/CodeGen/SelectionDAGISel.h"
#include "woruyuSubtarget.h"
#include <woruyu.h>
namespace llvm {
class woruyuDAGToDAGISel : public SelectionDAGISel {
private:
  // Include the pieces autogenerated from the target description.
  #include "woruyuGenDAGISel.inc"
public:
  explicit woruyuDAGToDAGISel(woruyuTargetMachine &TargetMachine,
                             CodeGenOpt::Level OptLevel)
      : SelectionDAGISel(TargetMachine, OptLevel) {}

  StringRef getPassName() const override {
    return "woruyu DAG->DAG Pattern Instruction Selection";
  }

  void Select(SDNode *N) override;
  bool SelectAddrFI(SDNode *Parent, SDValue N, SDValue &Base, SDValue &Offset);
};


} // namespace llvm

#endif// LLVM_LIB_TARGET_WORUYU_WORUYUISELDAGTODAG_H
